package javaPackage;

public class Java_substraction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=500;
		int b=300;
		int c=500;
		int sub=b-a;
		int sum=a+b;
		int sub1=a-c;
		int sum1=a+b+c;
		System.out.println(" Total " + sub);
        System.out.println(" Total " + sum);
        System.out.println(" Total " + sub1);
        System.out.println(" Total " + sum1);
        
	}

}
